let content = null;

if (items[0]?.json?.message?.content) {
  content = items[0].json.message.content;
} else if (items[0]?.json?.content) {
  content = items[0].json.content;
}

if (typeof content !== 'string') {
  throw new Error('No se encontró el contenido de OpenAI como string');
}

content = content.trim();

if (content.startsWith('```')) {
  content = content.replace(/^```[a-zA-Z]*\s*/,'');
  content = content.replace(/```$/,'');            
  content = content.trim();
}

content = content.replace(/,\s*([\]}])/g, '$1');

let data;
try {
  data = JSON.parse(content);
} catch (e) {
  throw new Error('No se pudo parsear la respuesta de OpenAI: ' + e.message + '\nContenido:\n' + content);
}

let mejor = data.mejor_transportista || null;
let comentarioGlobal = data.comentario_ia || null;
let listaCompleta = Array.isArray(data.lista_completa)
  ? data.lista_completa
  : [];

listaCompleta = listaCompleta
  .map((t) => ({
    transportista_id: Number(t.transportista_id),
    transportista_nombre: t.transportista_nombre,
    vehiculo_tipo: t.vehiculo_tipo,
    vehiculo_tonelaje: Number(t.vehiculo_tonelaje),
    probabilidad: Number(t.probabilidad),
    comentario_ia: t.comentario_ia,
  }))
  .sort((a, b) => (b.probabilidad || 0) - (a.probabilidad || 0));

if (!mejor && listaCompleta.length > 0) {
  mejor = {
    transportista_id: listaCompleta[0].transportista_id,
    transportista_nombre: listaCompleta[0].transportista_nombre,
    probabilidad: listaCompleta[0].probabilidad,
  };
}

return [
  {
    json: {
      mejor_transportista: mejor,
      comentario_ia: comentarioGlobal,
      lista_completa: listaCompleta,
    },
  },
];

const webhookItems = $items("Webhook");
const solicitudBody = webhookItems[0]?.json?.body ?? {};

const transportistasSQL = $items("Execute a SQL query");

const transportistas = transportistasSQL.map(i => ({
  transportista_id: parseInt(i.json.transportista_id, 10),
  transportista_nombre: i.json.transportista_nombre,
  transportista_apellido: i.json.transportista_apellido,
  vehiculo_id: parseInt(i.json.vehiculo_id, 10),
  vehiculo_tipo: i.json.vehiculo_tipo,
  vehiculo_observacion: i.json.observaciones,
  vehiculo_tonelaje: parseFloat(i.json.vehiculo_tonelaje),
  vehiculo_placa: i.json.vehiculo_placa,
}));

const disponibilidadSQL = $items("Execute a SQL query1");

const disponibilidad = disponibilidadSQL.map(i => ({
  transportista_id: parseInt(i.json.transportista_id, 10),
  fechas_ocupado: i.json.fechas_ocupado || [],
  viajes_semana: parseInt(i.json.viajes_semana, 10) || 0,
}));

const candidatos = transportistas.map(t => {
  const dispo = disponibilidad.find(d => d.transportista_id === t.transportista_id);

  return {
    ...t,
    fechas_ocupado: dispo?.fechas_ocupado ?? [],
    viajes_semana: dispo?.viajes_semana ?? 0,
  };
});

return [
  {
    json: {
      solicitud: {
        id_solicitud: solicitudBody.id_solicitud ?? null,
        tipo_carga: solicitudBody.tipo_carga ?? null,
        origen: solicitudBody.origen ?? null,
        destino: solicitudBody.destino ?? null,
        fecha_solicitud: solicitudBody.fecha_solicitud ?? null,
      },
      candidatos
    },
  },
];
